bool prime(int x) {
	int count, i;
	for(i=2;i<=x;i++) {
		if (x%i==0)
			count++;
			break;
	}
	if (count>1) 
		return true;
}
int main () {
	int a;
	cout<<"\nEnter an integer:";
	cin>>a;
	if ((a==1)||(a==0))
		cout<<"\nneither prime nor composite.";
	else if (prime(a))
		cout<<"\nPrime.";
	else 
		cout<<"\nComposite.";
	return 0;
}

