using namespace std; 

int size = 5;

int intToAscii(int number){
	return '0'+ number;
}

bool isInAssending(int *arr){

	int largest = arr[0];
	for(int i = 1; i < size; i++){
		if(arr[i] < largest){
			largest = arr[i];
		}else if(largest < arr[i]){
			return false;			
		}
	}
	return true;
}

